import React from 'react';
import { Card, CardContent, Typography, Divider } from '@mui/material';

export default function Account({ user }) {
  return (
    <Card className="account">
      <CardContent>
        <Typography variant="h5" className="name">
          {user.name}
        </Typography>

        <Typography variant="body1">
          Account Number: {user.accountNumber}
        </Typography>

        <Typography variant="body1">
          Routing Number: {user.routingNumber}  
        </Typography>

        <Divider />

        <Typography variant="h6" className="balance">
          Balance: ${user.balance}
        </Typography>

        {user.transactions.map(t => (
          <div key={t.date} className="transaction">
            <Typography variant="body2" color="textSecondary" className="transaction-date">
              {t.date}
            </Typography>

            <Typography variant="body1">
              {t.description}
            </Typography>

            <Typography variant="body1" className="transaction-amount">
              {t.amount > 0 ? `+$${t.amount}` : `-$${t.amount}`}
            </Typography>
          </div>
        ))}
      </CardContent>
    </Card>
  );
}
